select
	AVG(length)
from (
	SELECT (TIME_TO_SEC(end_time) - TIME_TO_SEC(start_time))/60 
	as length
	from growth_sessions
) as gs_length;

SELECT COUNT(id) count
FROM growth_sessions
WHERE date BETWEEN '$from' AND '$to';

SELECT COUNT(DISTINCT github_nickname)
FROM users
WHERE is_vehikl_member = 0;

SELECT COUNT(is_public) public
FROM growth_sessions gs
WHERE gs.date BETWEEN '$from' AND '$to' AND is_public = 1;

SELECT COUNT(is_public) private
FROM growth_sessions gs
WHERE gs.date BETWEEN '$from' AND '$to' AND is_public = 0;
